		"com/dxc/test/controller/Object.controller.js": "/*global location*/\r\nsap.ui.define([\r\n\t\t\"com/dxc/test/controller/BaseController\",\r\n\t\t\"sap/ui/model/json/JSONModel\",\r\n\t\t\"sap/ui/core/routing/History\",\r\n\t\t\"com/dxc/test/model/formatter\"\r\n\t], function (\r\n\t\tBaseController,\r\n\t\tJSONModel,\r\n\t\tHistory,\r\n\t\tformatter\r\n\t) {\r\n\t\t\"use strict\";\r\n\r\n\t\treturn BaseController.extend(\"com.dxc.test.controller.Object\", {\r\n\r\n\t\t\tformatter: formatter,\r\n\r\n\t\t\t/* =========================================================== */\r\n\t\t\t/* lifecycle methods                                           */\r\n\t\t\t/* =========================================================== */\r\n\r\n\t\t\t/**\r\n\t\t\t * Called when the worklist controller is instantiated.\r\n\t\t\t * @public\r\n\t\t\t */\r\n\t\t\tonInit : function () {\r\n\t\t\t\t// Model used to manipulate control states. The chosen values make sure,\r\n\t\t\t\t// detail page is busy indication immediately so there is no break in\r\n\t\t\t\t// between the busy indication for loading the view's meta data\r\n\t\t\t\tvar iOriginalBusyDelay,\r\n\t\t\t\t\toViewModel = new JSONModel({\r\n\t\t\t\t\t\tbusy : true,\r\n\t\t\t\t\t\tdelay : 0\r\n\t\t\t\t\t});\r\n\r\n\t\t\t\tthis.getRouter().getRoute(\"object\").attachPatternMatched(this._onObjectMatched, this);\r\n\r\n\t\t\t\t// Store original busy indicator delay, so it can be restored later on\r\n\t\t\t\tiOriginalBusyDelay = this.getView().getBusyIndicatorDelay();\r\n\t\t\t\tthis.setModel(oViewModel, \"objectView\");\r\n\t\t\t\tthis.getOwnerComponent().getModel().metadataLoaded().then(function () {\r\n\t\t\t\t\t\t// Restore original busy indicator delay for the object view\r\n\t\t\t\t\t\toViewModel.setProperty(\"/delay\", iOriginalBusyDelay);\r\n\t\t\t\t\t}\r\n\t\t\t\t);\r\n\t\t\t},\r\n\r\n\t\t\t/* =========================================================== */\r\n\t\t\t/* event handlers                                              */\r\n\t\t\t/* =========================================================== */\r\n\r\n\t\t\t/**\r\n\t\t\t * Event handler when the share in JAM button has been clicked\r\n\t\t\t * @public\r\n\t\t\t */\r\n\t\t\tonShareInJamPress : function () {\r\n\t\t\t\tvar oViewModel = this.getModel(\"objectView\"),\r\n\t\t\t\t\toShareDialog = sap.ui.getCore().createComponent({\r\n\t\t\t\t\t\tname: \"sap.collaboration.components.fiori.sharing.dialog\",\r\n\t\t\t\t\t\tsettings: {\r\n\t\t\t\t\t\t\tobject:{\r\n\t\t\t\t\t\t\t\tid: location.href,\r\n\t\t\t\t\t\t\t\tshare: oViewModel.getProperty(\"/shareOnJamTitle\")\r\n\t\t\t\t\t\t\t}\r\n\t\t\t\t\t\t}\r\n\t\t\t\t\t});\r\n\t\t\t\toShareDialog.open();\r\n\t\t\t},\r\n\r\n\t\t\t/**\r\n\t\t\t * Event handler  for navigating back.\r\n\t\t\t * It there is a history entry or an previous app-to-app navigation we go one step back in the browser history\r\n\t\t\t * If not, it will replace the current entry of the browser history with the worklist route.\r\n\t\t\t * @public\r\n\t\t\t */\r\n\t\t\tonNavBack : function() {\r\n\t\t\t\tvar sPreviousHash = History.getInstance().getPreviousHash(),\r\n\t\t\t\t\toCrossAppNavigator = sap.ushell.Container.getService(\"CrossApplicationNavigation\");\r\n\r\n\t\t\t\tif (sPreviousHash !== undefined || !oCrossAppNavigator.isInitialNavigation()) {\r\n\t\t\t\t\thistory.go(-1);\r\n\t\t\t\t} else {\r\n\t\t\t\t\tthis.getRouter().navTo(\"worklist\", {}, true);\r\n\t\t\t\t}\r\n\t\t\t},\r\n\r\n\t\t\t/* =========================================================== */\r\n\t\t\t/* internal methods                                            */\r\n\t\t\t/* =========================================================== */\r\n\r\n\t\t\t/**\r\n\t\t\t * Binds the view to the object path.\r\n\t\t\t * @function\r\n\t\t\t * @param {sap.ui.base.Event} oEvent pattern match event in route 'object'\r\n\t\t\t * @private\r\n\t\t\t */\r\n\t\t\t_onObjectMatched : function (oEvent) {\r\n\t\t\t\tvar sObjectId =  oEvent.getParameter(\"arguments\").objectId;\r\n\t\t\t\tthis.getModel().metadataLoaded().then( function() {\r\n\r\n\t\t\t\t\tvar sObjectPath = this.getModel().createKey(\"UserSet\", {\r\n\t\t\t\t\t\tUserName :  sObjectId\r\n\t\t\t\t\t});\r\n\t\t\t\t\t\r\n\t\t\t\t\tthis._bindView(\"/\" + sObjectPath);\r\n\r\n\t\t\t\t}.bind(this));\r\n\t\t\t},\r\n\r\n\t\t\t/**\r\n\t\t\t * Binds the view to the object path.\r\n\t\t\t * @function\r\n\t\t\t * @param {string} sObjectPath path to the object to be bound\r\n\t\t\t * @private\r\n\t\t\t */\r\n\t\t\t_bindView : function (sObjectPath) {\r\n\t\t\t\tvar oViewModel = this.getModel(\"objectView\"),\r\n\t\t\t\t\toDataModel = this.getModel();\r\n\r\n\t\t\t\tthis.getView().bindElement({\r\n\t\t\t\t\tpath: sObjectPath,\r\n\t\t\t\t\tparameters: {\r\n\t\t\t\t\t\texpand: \"ContactSet\"\r\n\t\t\t\t\t},\r\n\t\t\t\t\tevents: {\r\n\t\t\t\t\t\tchange: this._onBindingChange.bind(this),\r\n\t\t\t\t\t\tdataRequested: function () {\r\n\t\t\t\t\t\t\toDataModel.metadataLoaded().then(function () {\r\n\t\t\t\t\t\t\t\t// Busy indicator on view should only be set if metadata is loaded,\r\n\t\t\t\t\t\t\t\t// otherwise there may be two busy indications next to each other on the\r\n\t\t\t\t\t\t\t\t// screen. This happens because route matched handler already calls '_bindView'\r\n\t\t\t\t\t\t\t\t// while metadata is loaded.\r\n\t\t\t\t\t\t\t\toViewModel.setProperty(\"/busy\", true);\r\n\t\t\t\t\t\t\t});\r\n\t\t\t\t\t\t},\r\n\t\t\t\t\t\tdataReceived: function () {\r\n\t\t\t\t\t\t\toViewModel.setProperty(\"/busy\", false);\r\n\t\t\t\t\t\t}\r\n\t\t\t\t\t}\r\n\t\t\t\t});\r\n\t\t\t},\r\n\r\n\t\t\t_onBindingChange : function () {\r\n\t\t\t\tvar oView = this.getView(),\r\n\t\t\t\t\toViewModel = this.getModel(\"objectView\"),\r\n\t\t\t\t\toElementBinding = oView.getElementBinding();\r\n\r\n\t\t\t\t// No data for the binding\r\n\t\t\t\tif (!oElementBinding.getBoundContext()) {\r\n\t\t\t\t\tthis.getRouter().getTargets().display(\"objectNotFound\");\r\n\t\t\t\t\treturn;\r\n\t\t\t\t}\r\n\r\n\t\t\t\tvar oResourceBundle = this.getResourceBundle(),\r\n\t\t\t\t\toObject = oView.getBindingContext().getObject(),\r\n\t\t\t\t\tsObjectId = oObject.UserName,\r\n\t\t\t\t\tsObjectName = oObject.Nickname;\r\n\r\n\t\t\t\t// Everything went fine.\r\n\t\t\t\toViewModel.setProperty(\"/busy\", false);\r\n\t\t\t\toViewModel.setProperty(\"/saveAsTileTitle\", oResourceBundle.getText(\"saveAsTileTitle\", [sObjectName]));\r\n\t\t\t\toViewModel.setProperty(\"/shareOnJamTitle\", sObjectName);\r\n\t\t\t\toViewModel.setProperty(\"/shareSendEmailSubject\",\r\n\t\t\t\toResourceBundle.getText(\"shareSendEmailObjectSubject\", [sObjectId]));\r\n\t\t\t\toViewModel.setProperty(\"/shareSendEmailMessage\",\r\n\t\t\t\toResourceBundle.getText(\"shareSendEmailObjectMessage\", [sObjectName, sObjectId, location.href]));\r\n\t\t\t}\r\n\r\n\t\t});\r\n\r\n\t}\r\n);",
